export declare const TaskFormStyle = "\n\n  .task-form {\n    overflow: visible;\n  }\n\n  .task-form .form-group {\n    margin-bottom: 18px;\n    position: relative;\n  }\n\n  /* extra padding to last form group for breathing room */\n  .task-form .form-group:last-of-type {\n    margin-bottom: 24px;\n  }\n\n  .task-form label {\n    display: block;\n    margin-bottom: 5px;\n    font-weight: 500;\n    color: var(--text-color, #333);\n  }\n\n  .task-form input {\n    width: 100%;\n    padding: 8px;\n    border: 1px solid var(--dialog-border-color, #ddd);\n    border-radius: 4px;\n    font-size: 14px;\n    background-color: var(--dialog-bg-color, #fff);\n    color: var(--text-color, #333);\n  }\n\n  .task-form input:focus {\n    outline: none;\n    border-color: var(--button-bg-color, #0066cc);\n  }\n\n  .task-form .grid {\n    display: flex;\n    justify-content: space-between;\n    gap: 12px;\n  }\n\n  .task-form .grid .form-group {\n    flex: 1;\n  }\n\n  .task-form .form-actions {\n    display: flex;\n    justify-content: flex-end;\n    gap: 8px;\n    margin-top: 24px;\n    padding-top: 16px;\n    border-top: 1px solid var(--dialog-border-color, #eee);\n    position: sticky;\n    bottom: -16px;\n    background: var(--dialog-bg-color, white);\n    margin-left: -16px;\n    margin-right: -16px;\n    margin-bottom: -16px;\n    padding-left: 16px;\n    padding-right: 16px;\n    padding-bottom: 16px;\n  }\n\n  .task-form .btn-primary {\n    background: var(--button-bg-color, #0066cc);\n    color: var(--button-text-color, white);\n    border: none;\n    padding: 8px 16px;\n    border-radius: 4px;\n    cursor: pointer;\n    font-weight: 500;\n  }\n\n  .task-form .btn-primary:hover {\n    background: var(--button-hover-bg-color, #0052a3);\n  }\n\n  .task-form .btn-disabled,\n  .task-form .btn-disabled:hover {\n    background: #99c2ff;\n    cursor: not-allowed;\n    opacity: 0.7;\n  }\n\n  .task-form .form-error {\n    position: absolute;\n    bottom: -15px;\n    left: 0;\n    color: #dc3545;\n    font-size: 12px;\n    margin-top: 4px;\n    line-height: 1.2;\n    transition: opacity 0.2s ease;\n  }\n\n  .task-form .invalid {\n    border-color: #dc3545;\n  }\n\n  .task-form .invalid:focus {\n    border-color: #dc3545;\n    box-shadow: 0 0 0 0.2rem rgba(220, 53, 69, 0.25);\n  }\n\n  .color-picker-wrapper {\n    display: flex;\n    align-items: center;\n    gap: 8px;\n  }\n\n  .color-picker-wrapper input[type=\"color\"] {\n    -webkit-appearance: none;\n    width: 32px;\n    height: 32px;\n    padding: 0;\n    border: none;\n    border-radius: 4px;\n    cursor: pointer;\n  }\n\n  .color-picker-wrapper input[type=\"color\"]::-webkit-color-swatch-wrapper {\n    padding: 0;\n  }\n\n  .color-picker-wrapper input[type=\"color\"]::-webkit-color-swatch {\n    border: 1px solid var(--dialog-border-color, #ddd);\n    border-radius: 4px;\n  }\n\n  .color-picker-wrapper .color-preview {\n    font-size: 12px;\n    color: var(--text-color, #666);\n  }\n";
