export declare const TableStyle = "\n  .tasks-table,\n  .tasks-table th,\n  .tasks-table td {\n    border: var(--cell-border-width, 1px) solid var(--cell-border-color, #eff0f0);\n    border-collapse: collapse;\n  }\n\n  .tasks-table th {\n    height: 50px;\n  }\n\n  .tasks-table th,\n  .tasks-table td {\n    padding: 0 10px;\n    text-align: center;\n    white-space: nowrap;\n    overflow: hidden;\n    text-overflow: ellipsis;\n  }\n\n  .tasks-table .tasks-data-row .task-toggle-icon,\n  .tasks-table .tasks-data-row .task-toggle-icon-blank {\n    margin-right: 5px;\n    width: 10px;\n  }\n";
