export declare const TableStyle = "\n  .tasks-container {\n    height: 100%;\n    position: relative;\n    display: flex;\n    flex-direction: column;\n    overflow: hidden;\n  }\n\n  .tasks-header-table {\n    flex-shrink: 0;\n    overflow-x: auto;\n    overflow-y: hidden;\n    display: block;\n    background-color: var(--header-bg-color, #F3F3F3) !important;\n    scrollbar-width: none;\n    -ms-overflow-style: none;\n  }\n\n  .tasks-header-table::-webkit-scrollbar {\n    display: none;\n    height: 0;\n    width: 0;\n  }\n\n  /* Shadow DOM specific - hiding */\n  :host .tasks-header-table {\n    scrollbar-width: none !important;\n    -ms-overflow-style: none !important;\n  }\n\n  :host .tasks-header-table::-webkit-scrollbar {\n    display: none !important;\n    height: 0 !important;\n    width: 0 !important;\n  }\n\n  .tasks-header-table thead {\n    display: table;\n    width: 100%;\n    table-layout: fixed;\n  }\n\n  .tasks-body-wrapper {\n    flex: 1;\n    overflow: auto;\n    position: relative;\n  }\n\n  .tasks-table {\n    background-color: var(--background-color, #FFFFFF);\n    width: 100%;\n    table-layout: fixed;\n  }\n\n  .tasks-table,\n  .tasks-table th,\n  .tasks-table td {\n    border: var(--cell-border-width, 1px) solid var(--cell-border-color, #eff0f0);\n    border-collapse: collapse;\n  }\n\n  .tasks-table th {\n    height: 50px;\n    background-color: var(--header-bg-color, #F3F3F3);\n    color: var(--header-text-color, #333);\n  }\n\n  .tasks-table th,\n  .tasks-table td {\n    padding: 0 10px;\n    text-align: center;\n    white-space: nowrap;\n    overflow: hidden;\n    text-overflow: ellipsis;\n    color: var(--text-color, #000);\n  }\n\n  .tasks-table .tasks-data-row .task-toggle-icon,\n  .tasks-table .tasks-data-row .task-toggle-icon-blank {\n    display: inline-block;\n    margin-right: 5px;\n    width: 10px;\n    height: 10px;\n    vertical-align: middle;\n  }\n\n  .tasks-table .tasks-data-row .task-toggle-icon {\n    cursor: pointer;\n    position: relative;\n  }\n\n  /* chevron (expanded) */\n  .tasks-table .tasks-data-row .task-toggle-icon.expanded::before {\n    content: '';\n    position: absolute;\n    width: 6px;\n    height: 6px;\n    border-right: 2px solid var(--text-color, #000000);\n    border-bottom: 2px solid var(--text-color, #000000);\n    transform: rotate(45deg);\n    top: 0;\n    left: 2px;\n  }\n\n  /* chevron - pointing right (collapsed) */\n  .tasks-table .tasks-data-row .task-toggle-icon.collapsed::before {\n    content: '';\n    position: absolute;\n    width: 6px;\n    height: 6px;\n    border-right: 2px solid var(--text-color, #000000);\n    border-bottom: 2px solid var(--text-color, #000000);\n    transform: rotate(-45deg);\n    top: 2px;\n    left: 0;\n  }\n\n  .tasks-table .tasks-data-row .task-toggle-icon:hover::before {\n    opacity: 0.7;\n  }\n";
